mapper.select:
SET @user_lng = IFNULL((SELECT user_lng FROM fcm_info WHERE fcm_idx = IFNULL(#{fcm_idx}, '')), 128.729853); SET @user_lat = IFNULL((SELECT user_lat FROM fcm_info WHERE fcm_idx = IFNULL(#{fcm_idx}, '')), 36.568195);
SELECT DISTINCT st.str_idx , IFNULL(st.str_like_cnt, 0) AS str_like_cnt , st.str_lat , st.str_lng , ac.ct_ko_nm , CAST(ac.ct_parent AS SIGNED) AS ct_parent , (SELECT act.ct_ko_nm FROM ad_category act WHERE act.ct_idx = ac.ct_parent) AS store_type , ac.ct_ja_nm , ac.ct_zh_nm , ac.ct_es_nm , ac.ct_eu_nm , IFNULL(CONCAT(st.str_image), st.str_image) AS str_image , st.str_phone , st.str_category , IFNULL(st.str_website, '') AS str_website , asd.str_dt_name , asd.str_dt_type , IFNULL(asd.str_dt_address , '') AS str_dt_address , IFNULL(asd.str_dt_address_dt , '') AS str_dt_address_dt , IFNULL(asd.str_dt_desc , '') AS str_dt_desc , TRUNCATE( (6371 * acos( cos(radians(@user_lat)) * cos(radians(st.str_lat)) * cos(radians(st.str_lng) - radians(@user_lng)) + sin(radians(@user_lat)) * sin(radians(st.str_lat)))),1) AS str_km , IFNULL(st.operating_hour , '') AS operating_hour , (SELECT COUNT(*) FROM ad_review WHERE str_idx = st.str_idx ) AS str_review_cnt , TRUNCATE(IFNULL((SELECT AVG(review_avg) FROM ad_review WHERE str_idx = st.str_idx), 0.0), 1)AS review_ave , IFNULL( (SELECT like_status FROM ad_like WHERE mb_idx = IFNULL(#{mb_idx}, '') and str_idx = st.str_idx GROUP BY mb_idx), 'N') AS like_status FROM ad_store st LEFT JOIN ad_store_detail asd ON st.str_idx = asd.str_idx LEFT JOIN ad_category ac ON st.str_category = ac.ct_idx WHERE asd.str_dt_type = #{lang}
AND (ac.ct_parent = #{ct_idx} OR ac.ct_idx = #{ct_idx})
GROUP BY st.str_idx, asd.str_dt_type ORDER BY IFNULL(#{sorting},'')

SET @user_lng = IFNULL((SELECT user_lng FROM fcm_info WHERE fcm_idx = IFNULL(#{fcm_idx}, '')), 128.729853); SET @user_lat = IFNULL((SELECT user_lat FROM fcm_info WHERE fcm_idx = IFNULL(#{fcm_idx}, '')), 36.568195);
SELECT parking_mng_code , parking_name , parking_category , parking_type , parking_address , parking_lat , parking_lng , parking_hour , CONCAT('평일 : ', week_start_time, '~', week_end_time) AS week_time , week_start_time , week_end_time , CONCAT('토요일 : ', satur_start_time, '~', satur_end_time) AS satur_time , satur_start_time , satur_end_time , CONCAT('공휴일 : ', holiday_start_time, '~', holiday_end_time) AS holiday_time , holiday_start_time , holiday_end_time , parking_base_time , parking_base_fee , parking_free_check , TRUNCATE( (6371 * acos( cos(radians(@user_lat)) * cos(radians(parking_lat)) * cos(radians(parking_lng) - radians(@user_lng)) + sin(radians(@user_lat)) * sin(radians(parking_lat)) )), 1 ) AS parking_km ,CASE WHEN parking_base_fee = 0 THEN parking_free WHEN parking_base_fee != 0 THEN CONCAT(parking_free ,' ' ,parking_base_time,'분 ', parking_base_fee, '원 ') ELSE '현장' END AS parking_fee_info , parking_free FROM ad_parking
WHERE parking_free_check = #{parking_free_check}
