mapper.select:
SELECT
    ${mb_param} = ${mb_param}
FROM ad_member
WHERE mb_status = 1
  AND ${mb_param} = #{mb_value}

SELECT
    ${mb_param}
FROM ad_member
WHERE mb_status = 1
  AND mb_idx = #{mb_idx}

