mapper.select:
SET @user_lng = IFNULL((SELECT user_lng FROM fcm_info WHERE fcm_idx = IFNULL(#{fcm_idx}, '')), 128.729853); SET @user_lat = IFNULL((SELECT user_lat FROM fcm_info WHERE fcm_idx = IFNULL(#{fcm_idx}, '')), 36.568195);
SELECT * FROM( SELECT DISTINCT st.str_idx AS str_idx , IFNULL((SELECT COUNT(al.like_status) FROM ad_like al WHERE al.str_idx = st.str_idx AND al.like_type = 'S' AND al.like_status = 'Y'), 0) AS str_like_cnt , st.str_lat , st.str_lng , st.str_category , IFNULL((SELECT TRUNCATE(AVG(review_avg), 1) FROM ad_review WHERE str_idx = st.str_idx), 0.0)AS review_ave , ac.ct_ko_nm , ac.ct_ja_nm , ac.ct_zh_nm , ac.ct_es_nm , ac.ct_eu_nm , ac.ct_de_nm , asd.str_dt_type AS str_dt_type , asd.str_dt_name AS str_dt_name , IFNULL(asd.str_dt_address, '') AS str_dt_address , IFNULL(st.operating_hour, '') AS operating_hour , IFNULL(CONCAT(st.str_image), st.str_image) AS str_image , IFNULL(asd.str_dt_desc , '') AS str_dt_desc , (SELECT COUNT(*) FROM ad_review WHERE str_idx = st.str_idx ) AS str_review_cnt , TRUNCATE( (6371 * acos( cos(radians(@user_lat)) * cos(radians(st.str_lat)) * cos(radians(st.str_lng) - radians(@user_lng)) + sin(radians(@user_lat)) * sin(radians(st.str_lat)))),1) AS str_km , IFNULL((SELECT like_status FROM ad_like WHERE mb_idx = IFNULL(#{mb_idx}, '') and str_idx = st.str_idx), 'N') AS like_status FROM ad_store st LEFT JOIN ad_store_detail asd ON st.str_idx = asd.str_idx LEFT JOIN ad_category ac ON st.str_category = ac.ct_idx WHERE asd.str_dt_type = #{lang}
AND (ac.ct_parent = #{str_category} OR st.str_category = #{str_category})
AND st.str_category = #{str_category}
) AS list GROUP BY list.str_idx, list.str_dt_type ORDER BY ${sorting} DESC

SET @user_lng = IFNULL((SELECT user_lng FROM fcm_info WHERE fcm_idx = IFNULL(#{fcm_idx}, '')), 128.729853); SET @user_lat = IFNULL((SELECT user_lat FROM fcm_info WHERE fcm_idx = IFNULL(#{fcm_idx}, '')), 36.568195);
SELECT DISTINCT st.str_idx AS str_idx , IFNULL((SELECT COUNT(al.like_status) FROM ad_like al WHERE al.str_idx = st.str_idx AND al.like_type = 'S' AND al.like_status = 'Y'), 0) AS str_like_cnt , IFNULL((SELECT TRUNCATE(AVG(review_avg), 1) FROM ad_review WHERE str_idx = st.str_idx), 0.0)AS review_ave , st.str_lat , st.str_lng , st.str_category , ac.ct_ko_nm , ac.ct_ja_nm , ac.ct_zh_nm , ac.ct_es_nm , ac.ct_de_nm , ac.ct_eu_nm , asd.str_dt_type AS str_dt_type , asd.str_dt_name AS str_dt_name , IFNULL(CONCAT(st.str_image), st.str_image) AS str_image , IFNULL(asd.str_dt_desc , '') AS str_dt_desc , (SELECT COUNT(*) FROM ad_review WHERE str_idx = st.str_idx ) AS str_review_cnt , 0.0 AS str_review_ave , TRUNCATE( (6371 * acos( cos(radians(@user_lat)) * cos(radians(st.str_lat)) * cos(radians(st.str_lng) - radians(@user_lng)) + sin(radians(@user_lat)) * sin(radians(st.str_lat)))),1) AS str_km , IFNULL( (SELECT like_status FROM ad_like WHERE mb_idx = IFNULL(#{mb_idx}, '') and str_idx = st.str_idx), 'N') AS like_status FROM ad_store st LEFT JOIN ad_store_detail asd ON st.str_idx = asd.str_idx LEFT JOIN ad_category ac ON st.str_category = ac.ct_idx WHERE ac.ct_parent = 7 AND asd.str_dt_type = #{lang} GROUP BY st.str_idx, asd.str_dt_type
ORDER BY RAND()
ORDER BY ${sorting} DESC
LIMIT #{count}

SET @user_lng = IFNULL((SELECT user_lng FROM fcm_info WHERE fcm_idx = IFNULL(#{fcm_idx}, '')), 128.729853); SET @user_lat = IFNULL((SELECT user_lat FROM fcm_info WHERE fcm_idx = IFNULL(#{fcm_idx}, '')), 36.568195);
SELECT CASE WHEN (ac.ct_parent = 11 or st.str_category = 11) THEN 'food' WHEN (ac.ct_parent = 10 or st.str_category = 10) THEN 'tour' WHEN (ac.ct_parent = 7 or st.str_category = 7) THEN 'exp' WHEN ac.ct_parent = 3 or st.str_category = 3 THEN 'fest' WHEN ac.ct_parent = 5 or st.str_category = 5 THEN 'hotel' ELSE 'etc' END AS store_type, COALESCE(ch.chrt_age_group, '') AS chrt_age_group, COALESCE(ch.chrt_gender, '') AS chrt_gender, st.str_idx, st.str_lat, st.str_lng, st.str_category, ac.ct_ko_nm, ac.ct_ja_nm, ac.ct_zh_nm, ac.ct_es_nm, ac.ct_eu_nm, ac.ct_de_nm, asd.str_dt_type AS str_dt_type, asd.str_dt_name AS str_dt_name, IFNULL(asd.str_dt_address, '') AS str_dt_address, IFNULL(CONCAT( st.str_image), st.str_image) AS str_image, IFNULL(asd.str_dt_desc, '') AS str_dt_desc, TRUNCATE( (6371 * acos( cos(radians(@user_lat)) * cos(radians(st.str_lat)) * cos(radians(st.str_lng) - radians(@user_lng)) + sin(radians(@user_lat)) * sin(radians(st.str_lat)))),1) AS str_km FROM ad_use_chart ch LEFT JOIN ad_store st ON ch.str_idx = st.str_idx LEFT JOIN ad_store_detail asd ON ch.str_idx = asd.str_idx LEFT JOIN ad_category ac ON st.str_category = ac.ct_idx WHERE asd.str_dt_type = #{lang}
AND ch.chrt_gender = #{chrt_gender}
AND #{chrt_age_group} >= ch.chrt_age_group
GROUP BY st.str_idx, asd.str_dt_type ORDER BY COUNT(ch.chrt_idx) DESC LIMIT 0, #{count}
