mapper.update:
UPDATE ad_member
SET mb_pw = HEX(AES_ENCRYPT(#{mb_pw}, #{cryptKey}))
WHERE mb_idx = #{mb_idx}

UPDATE fcm_info
    SET fcm_token = #{fcm_token}
        , device_os_ver = #{device_os_ver}
        , device_name = #{device_name}
        , app_type = #{app_type}
      , app_ver = #{app_ver}
      , fcm_date = DATE_FORMAT(CURRENT_TIMESTAMP, '%Y/%m/%d %H:%i:%s')
WHERE mb_idx = #{mb_idx}
