mapper.update:
UPDATE ad_member
SET mb_pw = #{mb_pw}
WHERE mb_idx = #{mb_idx}

UPDATE fcm_info
SET mb_idx = #{mb_idx}
  , fcm_token = #{fcm_token}
  , device_os_ver = #{device_os_ver}
  , device_name = #{device_name}
  , app_type = #{app_type}
  , app_ver = #{app_ver}
  , fcm_date = DATE_FORMAT(CURRENT_TIMESTAMP, '%Y/%m/%d %H:%i:%s')
    , push_use = IFNULL(#{push_use}, 'Y')
    , marketing_use = IFNULL(#{marketing_use}, 'Y')
 , user_lng = #{user_lng}
, user_lat = #{user_lat}
WHERE fcm_idx = #{fcm_idx}

UPDATE fcm_info
    SET email_check = #{email_check}
WHERE fcm_idx = #{fcm_idx}
